import React from 'react'
import { Card,CardContent,Typography } from '@material-ui/core'


const InfoBox = ({title, cases, total}) => {

    const onCountryChange =  async (event) => {
        const countryCode = event.target.value //  which value(country) we select is now store in countryCode or at placeof worldwide
        setcountry(countryCode)
        const url = countryCode === "Worlwide" ? "https://disease.sh/v3/covid-19/all" : `https://disease.sh/v3/covid-19/countries/${countryCode}`

        // if sellect worldwide show all https://disease.sh/v3/covid-19/all
        // if select any country  https://disease.sh/v3/covid-19/country_code
        await fetch(url)
        .then((response) => response.json())
        .then((data)=>{
            setcountry(countryCode)
            setcountryInfo(data)
